import React, { FormEvent } from "react";
import "./styles.css";
import { Link } from "react-router-dom";
import { loginClient } from "../api";
import { toast } from "react-toastify";
import { useHistory } from "react-router";

// import { Container } from './styles';

const Login: React.FC = () => {
  const history = useHistory();

  const [cpf, setCpf] = React.useState("");
  const [password, setPassword] = React.useState("");

  const handleSubmit = async (e: FormEvent) => {
    e.preventDefault();
    const user = {cpf : cpf, password : password}
    if (!cpf || !password)
      return toast.error("Dados inválidos!");
    try {
      await loginClient(user);
      console.log(user)
      const userJson = JSON.stringify(user)
      localStorage.setItem('user', userJson);
      toast.success("Login efetuado com sucesso!");
      history.push("/orders");
    } catch (error) {
      console.log(error);
    }
  };
  return (
    <>
      <div className="geral">
        <div className="containerLogin">
          <h1 className="h1login">Login</h1>
          <form onSubmit={handleSubmit}>
            <p className="pinput">Usuário:</p>
            <input
              value={cpf}
              className="inputlogin placeholder-text"
              type="text"
              placeholder="Cpf"
              onChange={({target}) => setCpf(target.value)}
            />
            <p className="pinput">Senha:</p>
            <input
              value={password}
              className="inputlogin placeholder-text"
              type="password"
              placeholder="Senha"
              onChange={({target}) => setPassword(target.value)}
            />
            <button className="btnlogin" type='submit'>Entrar</button>
          </form>
          <Link to="/register">
            <h1 className="h1register">Cadastre-se</h1>
          </Link>
        </div>
      </div>
    </>
  );
};

export default Login;
